import { useState } from "react";
import { useLocation, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselNext,
  CarouselPrevious,
} from "@/components/ui/carousel";
import { MessageCircle, Phone, ArrowLeft, Loader2, Smartphone } from "lucide-react";
import { useAuth } from "@/context/AuthContext";
import UserMessageModal from "@/components/UserMessageModal";
import type { Gadget } from "@shared/schema";

export default function GadgetDetailPage() {
  const [location, navigate] = useLocation();
  const { user } = useAuth();
  const [imageLoadErrors, setImageLoadErrors] = useState<Set<number>>(new Set());

  const gadgetId = location.split("/")[2];

  const { data: gadget, isLoading, error} = useQuery<Gadget>({
    queryKey: ["/api/gadgets", gadgetId],
  });

  const handleImageError = (index: number) => {
    setImageLoadErrors(prev => new Set(prev).add(index));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen py-24 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-primary" />
      </div>
    );
  }

  if (error || !gadget) {
    return (
      <div className="min-h-screen py-24 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4" data-testid="text-error-title">Gadget not found</h1>
          <Link href="/gadgets">
            <Button data-testid="button-back-gadgets">Back to Gadgets</Button>
          </Link>
        </div>
      </div>
    );
  }

  const formattedPrice = `â‚¦${gadget.price.toLocaleString()}`;

  return (
    <div className="min-h-screen py-24">
      <div className="max-w-6xl mx-auto px-4">
        <button
          onClick={() => navigate("/gadgets")}
          className="flex items-center gap-2 text-primary hover:text-primary/80 mb-8 transition-colors"
          data-testid="button-back-detail"
        >
          <ArrowLeft className="w-4 h-4" />
          Back to Gadgets
        </button>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Image Carousel */}
          <div>
            {gadget.imageUrls && gadget.imageUrls.length > 0 ? (
              <Carousel className="w-full" data-testid="carousel-images">
                <CarouselContent>
                  {gadget.imageUrls.map((imageUrl, index) => (
                    <CarouselItem key={index}>
                      <Card className="overflow-hidden">
                        <div className="aspect-square bg-muted flex items-center justify-center">
                          {imageLoadErrors.has(index) || !imageUrl ? (
                            <Smartphone className="w-24 h-24 text-muted-foreground/30" />
                          ) : (
                            <img
                              src={imageUrl}
                              alt={`${gadget.name} - Image ${index + 1}`}
                              className="w-full h-full object-cover"
                              data-testid={`img-gadget-${index}`}
                              onError={() => handleImageError(index)}
                            />
                          )}
                        </div>
                      </Card>
                    </CarouselItem>
                  ))}
                </CarouselContent>
                {gadget.imageUrls.length > 1 && (
                  <>
                    <CarouselPrevious className="left-4" data-testid="button-prev-image" />
                    <CarouselNext className="right-4" data-testid="button-next-image" />
                  </>
                )}
              </Carousel>
            ) : (
              <Card className="overflow-hidden">
                <div className="aspect-square bg-muted flex items-center justify-center">
                  <Smartphone className="w-24 h-24 text-muted-foreground/30" />
                </div>
              </Card>
            )}

            {/* Image indicators */}
            {gadget.imageUrls && gadget.imageUrls.length > 1 && (
              <div className="flex justify-center gap-2 mt-4">
                {gadget.imageUrls.map((_, index) => (
                  <div
                    key={index}
                    className="w-2 h-2 rounded-full bg-primary/30"
                    data-testid={`indicator-${index}`}
                  />
                ))}
              </div>
            )}
          </div>

          {/* Details */}
          <div className="space-y-6">
            <div>
              <h1 className="text-4xl font-bold mb-2" data-testid="text-detail-name">{gadget.name}</h1>
              <div className="flex items-center gap-3">
                <span className="text-xs font-semibold px-3 py-1 rounded-full bg-primary/10 text-primary" data-testid="text-detail-condition">
                  {gadget.condition}
                </span>
              </div>
            </div>

            <div className="border-t pt-4">
              <p className="text-sm text-muted-foreground mb-2">Price</p>
              <p className="text-4xl font-bold text-green-600" data-testid="text-detail-price">{formattedPrice}</p>
            </div>

            {gadget.specs && gadget.specs.length > 0 && (
              <div>
                <h3 className="font-semibold mb-3">Key Specifications</h3>
                <ul className="space-y-2">
                  {gadget.specs.map((spec, index) => (
                    <li key={index} className="flex items-center gap-2 text-sm" data-testid={`spec-${index}`}>
                      <div className="w-1.5 h-1.5 rounded-full bg-primary" />
                      {spec}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {gadget.description && (
              <div>
                <h3 className="font-semibold mb-2">Description</h3>
                <p className="text-muted-foreground" data-testid="text-detail-description">{gadget.description}</p>
              </div>
            )}

            <div className="border-t pt-6 space-y-3">
              {user && !user.isAdmin ? (
                <>
                  <Button 
                    className="w-full bg-green-600 hover:bg-green-700" 
                    data-testid="button-contact-whatsapp"
                    onClick={() => window.open('https://wa.me/1234567890', '_blank')}
                  >
                    <Phone className="w-4 h-4 mr-2" />
                    Contact on WhatsApp
                  </Button>
                  <Button variant="outline" className="w-full" data-testid="button-open-chat-modal">
                    <MessageCircle className="w-4 h-4 mr-2" />
                    Chat with Admin
                  </Button>
                </>
              ) : (
                <Link href="/login">
                  <Button className="w-full" data-testid="button-login-to-contact">
                    Login to Contact Seller
                  </Button>
                </Link>
              )}
            </div>
          </div>
        </div>
      </div>

      {user && !user.isAdmin && <UserMessageModal />}
    </div>
  );
}

client/src/components/CryptoForm.tsx.content:
import { useState } from "react";
import { Card, CardHeader, CardContent, CardFooter } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Copy } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

const CRYPTO_COINS = ["Bitcoin", "USDT", "Ethereum", "USDC"];

export default function CryptoForm() {
  const [tradeType, setTradeType] = useState<"buy" | "sell">("sell");
  const [formData, setFormData] = useState({
    coin: "",
    amount: "",
    transactionHash: "",
    bankName: "",
    accountNumber: "",
    accountName: "",
    customerEmail: ""
  });
  const { toast } = useToast();

  const walletAddress = "1A1zP1eP5QGefi2DMPTfTL5SLmv7DivfNa";

  const submitMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/crypto-trades", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: `${tradeType === "buy" ? "Buy" : "Sell"} Request Submitted`,
        description: "We'll process your request shortly. Please check your email for updates.",
      });
      setFormData({
        coin: "",
        amount: "",
        transactionHash: "",
        bankName: "",
        accountNumber: "",
        accountName: "",
        customerEmail: ""
      });
    },
    onError: (error: any) => {
      toast({
        title: "Submission Failed",
        description: error.message || "Failed to submit request. Please try again.",
        variant: "destructive",
      });
    },
  });

  const copyToClipboard = () => {
    navigator.clipboard.writeText(walletAddress);
    toast({
      title: "Copied!",
      description: "Wallet address copied to clipboard",
    });
  };

  const handleSubmit = () => {
    submitMutation.mutate({
      tradeType,
      coin: formData.coin,
      amount: formData.amount,
      transactionHash: formData.transactionHash || null,
      bankName: formData.bankName || null,
      accountNumber: formData.accountNumber || null,
      accountName: formData.accountName || null,
      customerEmail: formData.customerEmail || null,
      status: "pending",
    });
  };

  return (
    <Card className="max-w-3xl mx-auto">
      <CardHeader>
        <h2 className="text-2xl font-semibold" data-testid="text-crypto-form-title">Crypto Trading</h2>
      </CardHeader>

      <CardContent className="space-y-6">
        <Tabs value={tradeType} onValueChange={(value) => setTradeType(value as "buy" | "sell")}>
          <TabsList className="grid w-full grid-cols-2" data-testid="tabs-trade-type">
            <TabsTrigger value="sell" data-testid="tab-sell">Sell to JTECH</TabsTrigger>
            <TabsTrigger value="buy" data-testid="tab-buy">Buy from JTECH</TabsTrigger>
          </TabsList>

          <TabsContent value="sell" className="space-y-4 mt-6">
            <div>
              <Label htmlFor="coin-sell">Cryptocurrency</Label>
              <Select value={formData.coin} onValueChange={(value) => setFormData(prev => ({ ...prev, coin: value }))}>
                <SelectTrigger id="coin-sell" data-testid="select-coin-sell">
                  <SelectValue placeholder="Select cryptocurrency" />
                </SelectTrigger>
                <SelectContent>
                  {CRYPTO_COINS.map(coin => (
                    <SelectItem key={coin} value={coin}>{coin}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="p-4 bg-muted rounded-md space-y-2">
              <Label>Send to this address:</Label>
              <div className="flex items-center gap-2">
                <code className="flex-1 text-sm p-2 bg-background rounded border break-all" data-testid="text-wallet-address">{walletAddress}</code>
                <Button size="icon" variant="outline" onClick={copyToClipboard} data-testid="button-copy-address">
                  <Copy className="w-4 h-4" />
                </Button>
              </div>
            </div>

            <div>
              <Label htmlFor="amount-sell">Amount Sent</Label>
              <Input
                id="amount-sell"
                type="number"
                placeholder="Enter amount"
                value={formData.amount}
                onChange={(e) => setFormData(prev => ({ ...prev, amount: e.target.value }))}
                data-testid="input-amount-sell"
              />
            </div>

            <div>
              <Label htmlFor="transactionHash">Transaction Hash</Label>
              <Input
                id="transactionHash"
                placeholder="Enter transaction hash"
                value={formData.transactionHash}
                onChange={(e) => setFormData(prev => ({ ...prev, transactionHash: e.target.value }))}
                data-testid="input-transaction-hash"
              />
            </div>

            <div>
              <Label htmlFor="customerEmail-sell">Email (Optional)</Label>
              <Input
                id="customerEmail-sell"
                type="email"
                placeholder="your@email.com"
                value={formData.customerEmail}
                onChange={(e) => setFormData(prev => ({ ...prev, customerEmail: e.target.value }))}
                data-testid="input-customer-email-sell"
              />
            </div>

            <div>
              <Label htmlFor="bankName-sell">Bank Name</Label>
              <Input
                id="bankName-sell"
                placeholder="Enter bank name for payout"
                value={formData.bankName}
                onChange={(e) => setFormData(prev => ({ ...prev, bankName: e.target.value }))}
                data-testid="input-bank-name-sell"
              />
            </div>

            <div>
              <Label htmlFor="accountNumber-sell">Account Number</Label>
              <Input
                id="accountNumber-sell"
                placeholder="Enter account number"
                value={formData.accountNumber}
                onChange={(e) => setFormData(prev => ({ ...prev, accountNumber: e.target.value }))}
                data-testid="input-account-number-sell"
              />
            </div>
          </TabsContent>

          <TabsContent value="buy" className="space-y-4 mt-6">
            <div>
              <Label htmlFor="coin-buy">Cryptocurrency</Label>
              <Select value={formData.coin} onValueChange={(value) => setFormData(prev => ({ ...prev, coin: value }))}>
                <SelectTrigger id="coin-buy" data-testid="select-coin-buy">
                  <SelectValue placeholder="Select cryptocurrency" />
                </SelectTrigger>
                <SelectContent>
                  {CRYPTO_COINS.map(coin => (
                    <SelectItem key={coin} value={coin}>{coin}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="amount-buy">Amount to Buy</Label>
              <Input
                id="amount-buy"
                type="number"
                placeholder="Enter amount"
                value={formData.amount}
                onChange={(e) => setFormData(prev => ({ ...prev, amount: e.target.value }))}
                data-testid="input-amount-buy"
              />
            </div>

            <div>
              <Label htmlFor="customerEmail-buy">Email (Optional)</Label>
              <Input
                id="customerEmail-buy"
                type="email"
                placeholder="your@email.com"
                value={formData.customerEmail}
                onChange={(e) => setFormData(prev => ({ ...prev, customerEmail: e.target.value }))}
                data-testid="input-customer-email-buy"
              />
            </div>

            <div className="p-4 bg-muted rounded-md">
              <p className="text-sm text-muted-foreground">
                After submitting, we'll send you our bank details for payment. 
                We'll transfer the crypto to your wallet once payment is confirmed.
              </p>
            </div>
          </TabsContent>
        </Tabs>
      </CardContent>

      <CardFooter>
        <Button onClick={handleSubmit} disabled={submitMutation.isPending} className="w-full" data-testid="button-submit-crypto">
          {submitMutation.isPending ? "Submitting..." : "Submit Request"}
        </Button>
      </CardFooter>
    </Card>
  );
}

